import React, { Component } from 'react';
import { View, Text, StyleSheet, Image, Button} from 'react-native';

export default class Projeto extends Component {

   constructor(props){
     super(props);
     this.state = {imagem};

     this.mudarImagem = this.mudarImagem.bind(this);
  }

  render(){
    return(
      <View style={styles.body}>

        <Image style={styles.logo} source={require('./img/nirvana_logo.png')}/>

        <View>
          <Text style={styles.titulo}>Escolha uma das opções</Text>
        </View>

        <View style={styles.areaA}>
          <View style={styles.cima}>
            <Button style={styles.botao} title="Guitarrista" onClick={this.mudarImagem}/>
            <Button  style={styles.botao} title="Baixista" onClick={this.mudarImagem}/>
          </View>
        </View>

        <View>

          <Text>....................</Text>

        </View>

        <View style={styles.areaB}>
          <View style={styles.baixo}>
            <Button style={styles.botao} title="Baterista" onClick={this.mudarImagem}/>
            <Button style={styles.botao} title="2° Guitarrista" onClick={this.mudarImagem}/>
          </View>
        </View>
        <View style={styles.photo}>
          <Text>{this.state.imagem()}</Text>
        </View>

        <View>
          <Text style={styles.pe}>Logo da banda</Text>
        </View>

      </View>
    );
  }
}

const styles = StyleSheet.create({
  body:{
    paddingTop: 45,
    backgroundColor: '#000000',
    flex: 3,
    alignItems: 'center',
    flexDirection: 'column',

  },
   logo:{
    height: 100,
    width: 300,
    marginTop: 10,
    zIndex:0

  },
  titulo:{
    padding: 20,
    color: '#ffffff',
    fontSize: 20,

  },
  cima:{
    flexDirection: 'row',

  },

  baixo:{
    flexDirection: 'row',

  },
  botao:{
    justifyContent: 'space-around',

  },
  smile:{
    height: 300,
    width: 300,
    marginTop: 10,
    zIndex:0,
    alignItems: 'flex-end',

  },
  photo:{
    padding: 80

  },
  pe:{
    color: '#ffffff',
    fontSize: 18
  }
})
