import React, { Component } from 'react';
import { View, Text, StyleSheet, Image, TouchableOpacity} from 'react-native';

export default class Projeto extends Component {

  // constructor(props){
  //   super(props);
  //   this.state = {texto1: 'Texto 1', texto2: 'Texto 2'};

  //   this.mudarImagem = this.mudarImagem.bind(this);
  //}

  render(){
    return(
      <View style={styles.body}>

        <Image style={styles.logo} source={require('./img/nirvana_logo.png')}/>

        <View>
          <Text style={styles.titulo}>Escolha uma das opções</Text>
        </View>


          <View style={styles.cima}>
            <TouchableOpacity style={styles.botao} title="Guitarrista">Guitarrista</TouchableOpacity>
            <TouchableOpacity  style={styles.botao} title="Baixista">Baixista</TouchableOpacity>
            <TouchableOpacity style={styles.botao} title="Guitarrista">Baterista</TouchableOpacity>
            <TouchableOpacity  style={styles.botao} title="Baixista">Baixista</TouchableOpacity>
          </View>

        <View style={styles.photo}>
          <Image style={styles.smile} source={require('./img/smile.png')}/>
        </View>

        <View>
          <Text style={styles.pe}>Logo da banda</Text>
        </View>

      </View>
    );
  }
}

const styles = StyleSheet.create({
  body:{
    paddingTop: 45,
    backgroundColor: '#000000',
    flex: 3,
    alignItems: 'center',
    flexDirection: 'column',

  },
   logo:{
    height: 100,
    width: 300,
    marginTop: 10,
    zIndex:0

  },
  titulo:{
    padding: 20,
    color: '#ffffff',
    fontSize: 20,

  },
  cima:{
    flexDirection: 'row',

  },
  botao:{
    alignContent: 'space-around',
    backgroundColor: '#1e90ff',
    borderRadius: 50,
    padding: 10,
    margin: 10,
    flexBasis: 90,
    justifyContent: 'space-around',
    alignItems: 'baseline',



  },
  smile:{
    height: 300,
    width: 300,
    marginTop: 10,
    zIndex:0,
    alignItems: 'flex-end',

  },
  photo:{
    padding: 40

  },
  pe:{
    color: '#ffffff',
    fontSize: 18
  }
})
